import React, { useState } from 'react';
import { images } from '../../constants';
import './Navbar.scss';
import { HiMenuAlt4, HiX } from 'react-icons/hi';
import { motion } from 'framer-motion';

const Navbar = () => {
  const [toggle, setToggle] = useState(false);

  const openPDFInNewTab = () => {
    
    const pdfUrl = '../../../assets/JeffreyResume.pdf';
    
    // Set content-type as application/pdf in the response header
    const options = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/pdf',
      },
    };

    // Open the PDF in a new tab
    fetch(pdfUrl, options)
      .then(response => response.blob())
      .then(blob => {
        const url = window.URL.createObjectURL(new Blob([blob]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', '2024Resume.pdf');
        document.body.appendChild(link);
        link.click();
        link.parentNode.removeChild(link);
      })
      .catch(error => console.error('Error fetching the PDF:', error));
  };

  return (
    <nav className="app__navbar">
      <div className="app__navbar-logo">
        <img src={images.logo} alt="logo" />
      </div>
      <ul className="app__navbar-links">
        {['home', 'about', 'work', 'skills', 'contact', 'resume'].map((item) => (
          <li className="app__flex p-text" key={`link-${item}`}>
            <a href={`#${item}`} onClick={() => setToggle(false)}>
              {item === 'resume' ? (
                <span onClick={openPDFInNewTab}>{item}</span>
              ) : (
                item
              )}
            </a>
            <div />
          </li>
        ))}
      </ul>

      <div className="app__navbar-menu">
        <HiMenuAlt4 onClick={() => setToggle(true)} />

        {toggle && (
          <motion.div
            whileInView={{ x: [300, 0] }}
            transition={{ duration: 0.85, ease: 'easeOut' }}
          >
            <HiX onClick={() => setToggle(false)} />
            <ul>
              {['home', 'about', 'work', 'skills', 'contact', 'resume'].map((item) => (
                <li key={item}>
                  <a href={`#${item}`} onClick={() => setToggle(false)}>
                    {item === 'resume' ? (
                      <span onClick={openPDFInNewTab}>{item}</span>
                    ) : (
                      item
                    )}
                  </a>
                </li>
              ))}
            </ul>
          </motion.div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
