import React, {useState, useEffect} from 'react';
import {motion} from 'framer-motion';
import './About.scss';
import {images} from '../../constants'
const abouts = [
  {title: 'Web Developement', description: 'I am a good web developer.', imgUrl: images.about01},
  {title: 'UI/UX', description: 'I am a good web developer.', imgUrl: images.about02},
  {title: 'Backend', description: 'I am a good web developer.', imgUrl: images.about03},
  {title: 'Fullstack', description: 'I am a good web developer.', imgUrl: images.about04}
]

const About = () => {
  return (
    <>
      <h2 className='head-text'>
        I Know That
        <span>
          Good Design
        </span>
        <br />
        means
        <span>
          Good Business
        </span>
      </h2>

      <div className='app__profiles'>
        {abouts.map((about,index) => (
          <motion.div 
            whileInView={{opacity: 1}}
            whileHover={{scale: 1.1}}
            transition = {{duration:0.5, type: 'tween' }}
            className='app__profile-item'
            key={about.title + index}
          >
            <img src={about.imgUrl} alt={about.title} />
            <h2 className='bold-text' style={{marginTop: 20}}>
              {about.title};
            </h2>
            <p className='p-text' style={{marginTop: 10}}>
              {about.description};
            </p>
          </motion.div>
        ))}
      </div>
    </>
  )
}

export default About
