import React, { useState} from 'react'
import {images} from '../../constants';
import { AppWrap, MotionWrap } from '../../wrapper';
import {client} from '../../client'
import './Footer.scss';

const Footer = () => {
  const [formData, setFormData] = useState({ names: '', email: '', message: '' });
  const [isFormSubmitted, setIsFormSubmitted] = useState(false);
  const [loading, setLoading] = useState(false);

  const { username, email, message } = formData;

  const handleChangeInput = (e) => {
    const { names, value } = e.target;
    setFormData({ ...formData, [names]: value });
  };

  const handleSubmit = () => {
    setLoading(true);

    const contact = {
      _type: 'contact',
      name: formData.username,
      email: formData.email,
      message: formData.message,
    };

    client.create(contact)
      .then(() => {
        setLoading(false);
        setIsFormSubmitted(true);
      })
      .catch((err) => console.log(err));
  };


  return (
    <>
      <h2 className='head-text'>
        Let's Chat!
      </h2>

      <div className='app__footer-cards'>
        <div className='app__footer_card'>
          <img src={images.email} alt='email' />
          <a href='mailto:jeffrey.huang.yz@gmail.com' className='p-text'>
            jeffrey.huang.yz@gmail.com
          </a>
        </div>
        
        <div className='app__footer_card'>
          <img src={images.mobile} alt='mobile' />
          <a href='tel: +1 (647) 639-3586' className='p-text'>
            +1 (647) 639-3586
          </a>
        </div>
      </div>

      <div className='app__footer-form app__flex'>
        <div className='app__flex'>
          <input className='p-text' type='text' placeholder='Your Name' name='name' value={names} onChange={handleChangeInput} />
          <input className='p-text' type='text' placeholder='Your Email' name='email' value={email} onChange={handleChangeInput} />
        </div>  

        <div>
          <textarea className='p-text' placeholder='Your Message' value={message} name='message' onChange={handleChangeInput} />
        </div>

        <button type='button' className='p-text' onClick={handleSubmit}>
          Send Message
        </button>
      </div> 
    </>
  )
}

export default AppWrap(
  MotionWrap(Footer, 'app__footer'),
  'contact',
  'app__whitebg'
)
